import React from "react";
import { Card, Button } from "react-bootstrap";

const ProyectosPersonales = (props) => (
 <div>
  <Card
    style={{ width: "80%",  }}
    className="d-flex flex-col flex-sm-row mb-5 justify-content-center align-items-center"
  >
    <Card.Body>
      <Card.Title className="mt-2 mb-5 d-flex justify-content-center items-center h2 " style={{width: "100%", margin: "5px" }}>
        {props.titulo}
      </Card.Title>
      <Card.Text style={{ fontSize: "15px", margin: "5px" }}>
        {props.descripcion}
      </Card.Text>
    </Card.Body>
    <Card.Img className="d-flex justify-content-center align-items-center"
      variant="top"
      src={props.imagenUrl}
      style={{ width: "10rem", margin: "7px" }}
    />
      
      
  </Card>
  <Button variant="outline-info">Más información</Button>
  </div> 
);

export default ProyectosPersonales;
