import React from "react";
import { Jumbotron, Container } from "react-bootstrap";
import Footer from "./Footer";

class Estudios extends React.Component {
  constructor() {
    super();
    this.state = {
      tarjetas: [
        {
          titulo: "HTML",
          imagenUrl: LogoHtml,
          descripcion: "",
        },
        {
          titulo: "CSS",
          imagenUrl: LogoCss,
          descripcion: "",
        },

        {
          titulo: "Javascript",
          imagenUrl: LogoJavascript,
          descripcion: "",
        },

        {
          titulo: "React",
          imagenUrl: LogoReact,
          descripcion: "",
        },

        {
          titulo: "GitHub",
          imagenUrl: LogoGit,
          descripcion: "",
        },

        {
          titulo: "Bootstrap",
          imagenUrl: LogoBoostrap,
          descripcion: "",
        },
        {
          titulo: "Laravel",
          imagenUrl: LogoLaravel,
          descripcion: "",
        },
        {
          titulo: "PHP",
          imagenUrl: LogoPhp,
          descripcion: "",
        },

        {
          titulo: "Figma",
          imagenUrl: LogoFigma,
          descripcion: "",
        },

        {
          titulo: "Trello",
          imagenUrl: LogoTrello,
        },
      ],
    };
  }
  
  render() {
    return (
    <React.Fragment>
        <Jumbotron fluid>
          <Container>
            <h1>Estudios</h1>
            <p>
            “Siento que usted será algo esencial para lo que tengo que hacer, aunque todavía no me doy cuenta de la razón”, El túnel, Ernesto Sábato.
            </p>
          </Container>
        </Jumbotron>
        <Footer /> 
    </React.Fragment>
    );
}
}

export default Estudios;
