import React from "react";
import { Card } from "react-bootstrap";

const ProyectosPersonales = (props) => (
  <Card
    style={{ width: "80%",  }}
    className="d-flex flex-col flex-sm-row mb-5"
  >
    <Card.Body>
      <Card.Title className="m-3 d-flex justify-content-center items-center h2 " style={{width: "100%", margin: "5px" }}>
        {props.titulo}
      </Card.Title>
      <Card.Text style={{ fontSize: "15px", margin: "5px" }}>
        {props.descripcion}
      </Card.Text>
    </Card.Body>
    <Card.Img className="d-flex justify-content-center align-items-center"
      variant="top"
      src={props.imagenUrl}
      style={{ width: "15rem", marginTop: "7px" }}
    />
    
  </Card>
);

export default ProyectosPersonales;
