import React from "react";
import { Jumbotron, Container } from "react-bootstrap";
import Footer from "./Footer";

class Proyectos extends React.Component {
  constructor() {
    super();
    this.state = {
      tarjetas: [
        {
          titulo: "HTML",
          imagenUrl: LogoHtml,
          descripcion: "5 meses",
        },
        {
          titulo: "CSS",
          imagenUrl: LogoCss,
          descripcion: "5 meses",
        },

        {
          titulo: "Javascript",
          imagenUrl: LogoJavascript,
          descripcion: "4 meses",
        },

        {
          titulo: "React",
          imagenUrl: LogoReact,
          descripcion: "1 mes",
        },

        {
          titulo: "GitHub",
          imagenUrl: LogoGit,
          descripcion: "5 meses",
        },

        {
          titulo: "Bootstrap",
          imagenUrl: LogoBoostrap,
          descripcion: "2 meses",
        },
        {
          titulo: "Laravel",
          imagenUrl: LogoLaravel,
          descripcion: "2 meses",
        },
        {
          titulo: "PHP",
          imagenUrl: LogoPhp,
          descripcion: "2 meses",
        },

        {
          titulo: "Figma",
          imagenUrl: LogoFigma,
          descripcion: "5 meses",
        },

        {
          titulo: "Trello",
          imagenUrl: LogoTrello,
          descripcion: "5 meses",
        },

        {
          titulo: "Tailwinds",
          imagenUrl: LogoTailwinds,
          descripcion: "1 meses",
        },

        {
          titulo: "Saas",
          imagenUrl: LogoSaas,
          descripcion: "1 mes",
        },

        {
          titulo: "Vue",
          imagenUrl: LogoVue,
          descripcion: "1 mes",
        },

        {
          titulo: "Mysql",
          imagenUrl: LogoMysql,
          descripcion: "1 mes",
        },
      ],
    };
  }
  
  render() {
    return (
      <React.Fragment>
        <Jumbotron fluid>
          <Container className="p-4">
            <h1>Proyectos</h1>
            <p>
            "Para eso se necesita locura; locura, ¿oyes? ¡El todo por el todo!”, Zorba el griego, Nikos Kazantzakis
            </p>
          </Container>
        </Jumbotron>
        
        <div id="container">
          {this.state.tarjetas.map((tarjeta) => (
            <ProyectosPersonales
              imagenUrl={tarjeta.imagenUrl}
              titulo={tarjeta.titulo}
              descripcion={tarjeta.descripcion}
              
            />
          ))}
        </div>
        <Footer /> 
      </React.Fragment>
     
    );
  }
}

export default Proyectos;
