import React from "react";
import { Jumbotron, Container } from "react-bootstrap";
import Footer from "./Footer";
import ProyectosPersonales from "./ProyectosPersonales";
import Viaje from "../img/viaje.jpg";
import JumbotronReact from "./JumbotronReact";

class Proyectos extends React.Component {
  constructor() {
    super();
    this.state = {
      tarjetas: [
        {
          titulo: "Viaje por la última frontera de Europa",
          descripcion: "Un libro de aventuras con historia, política y reflexiones viajeras, sobre mis experiencias en un viaje de cuatro meses por las fronteras de Europa. Recorrí lugares poco transitados como Kaliningrado, Minsk, el este de Ucrania, Transnistria, Gagauzia, el País Sículo, el Kurdistán turco y Chipre del Norte.",
          imagenUrl: Viaje,

        },

        {
          titulo: "Viaje por la última frontera de Europa",
          descripcion: "Un libro de aventuras con historia, política y reflexiones viajeras, sobre mis experiencias en un viaje de cuatro meses por las fronteras de Europa. Recorrí lugares poco transitados como Kaliningrado, Minsk, el este de Ucrania, Transnistria, Gagauzia, el País Sículo, el Kurdistán turco y Chipre del Norte.",
          imagenUrl: Viaje,

        },

        {
          titulo: "Viaje por la última frontera de Europa",
          descripcion: "Un libro de aventuras con historia, política y reflexiones viajeras, sobre mis experiencias en un viaje de cuatro meses por las fronteras de Europa. Recorrí lugares poco transitados como Kaliningrado, Minsk, el este de Ucrania, Transnistria, Gagauzia, el País Sículo, el Kurdistán turco y Chipre del Norte.",
          imagenUrl: Viaje,

        },
        ],
    };
  }
  
  render() {
    return (
      <React.Fragment>
        <JumbotronReact />
        
        <div className="d-flex justify-content-center align-items-center flex-column mb-5" >
          {this.state.tarjetas.map((tarjeta) => (
            <ProyectosPersonales
              imagenUrl={tarjeta.imagenUrl}
              titulo={tarjeta.titulo}
              descripcion={tarjeta.descripcion}
              
            />
          ))}
        </div>
        <Footer /> 
      </React.Fragment>
     
    );
  }
}

export default Proyectos;
