import React from "react";
import {
  Jumbotron,
  Container,
  Card,
  Button,
  ProgressBar,
} from "react-bootstrap";
import Footer from "./Footer";
import SkillsPersonales from "./SkillsPersonales";
import Captura from "../img/Captura.png";
import LogoJavascript from "../img/javascript.png";
import LogoHtml from "../img/html.png";
import LogoCss from "../img/css.png";
import LogoReact from "../img/react.png";
import LogoGit from "../img/github.png";
import LogoBoostrap from "../img/bootstrap.png";
import LogoLaravel from "../img/laravel.png";
import LogoPhp from "../img/php.png";
import LogoFigma from "../img/figma.png";
import LogoTrello from "../img/trello.png";

class Skills extends React.Component {
  constructor() {
    super();
    this.state = {
      tarjetas: [
        {
          titulo: "HTML",
          imagenUrl: LogoHtml,
          descripcion: "",
        },
        {
          titulo: "CSS",
          imagenUrl: LogoCss,
          descripcion: "",
        },

        {
          titulo: "Javascript",
          imagenUrl: LogoJavascript,
          descripcion: "",
        },

        {
          titulo: "React",
          imagenUrl: LogoReact,
          descripcion: "",
        },

        {
          titulo: "GitHub",
          imagenUrl: LogoGit,
          descripcion: "",
        },

        {
          titulo: "Bootstrap",
          imagenUrl: LogoBoostrap,
          descripcion: "",
        },
        {
          titulo: "Laravel",
          imagenUrl: LogoLaravel,
          descripcion: "",
        },
        {
          titulo: "PHP",
          imagenUrl: LogoPhp,
          descripcion: "",
        },

        {
          titulo: "Figma",
          imagenUrl: LogoFigma,
          descripcion: "",
        },

        {
          titulo: "Trello",
          imagenUrl: LogoTrello,
        },
      ],
    };
  }
  render() {
    return (
      <React.Fragment>
        <Jumbotron fluid className="p-4">
          <Container>
            <h1>Skills</h1>
            <p>
              “Me había levantado para irme a casa; porque ya era tarde, y todo
              estaba oscuro”, El paseo, Robert Walser.
            </p>
          </Container>
        </Jumbotron>
        <div className="row">
        <div className="col-6">
          {this.state.tarjetas.map((tarjeta) => (
            <SkillsPersonales
              imagenUrl={tarjeta.imagenUrl}
              titulo={tarjeta.titulo}
            />
          ))}
        </div>
        <div className="col-6">
          {this.state.tarjetas.map((tarjeta) => (
            <SkillsPersonales
              imagenUrl={tarjeta.imagenUrl}
              titulo={tarjeta.titulo}
            />
          ))}
        </div>
        </div>
        <Footer />
      </React.Fragment>
    );
  }
}

export default Skills;
