import React from "react";
import SkillsPersonales from "./SkillsPersonales";

class Tarjeta extends React.Component {
    constructor() {
        super();
        this.state = {
          tarjetas: [
            {
              titulo: "HTML",
              imagenUrl: LogoHtml,
              descripcion: "",
            },
            {
              titulo: "CSS",
              imagenUrl: LogoCss,
              descripcion: "",
            },
    
            {
              titulo: "Javascript",
              imagenUrl: LogoJavascript,
              descripcion: "",
            },
    
            {
              titulo: "React",
              imagenUrl: LogoReact,
              descripcion: "",
            },
    
            {
              titulo: "GitHub",
              imagenUrl: LogoGit,
              descripcion: "",
            },
    
            {
              titulo: "Bootstrap",
              imagenUrl: LogoBoostrap,
              descripcion: "",
            },
            {
              titulo: "Laravel",
              imagenUrl: LogoLaravel,
              descripcion: "",
            },
            {
              titulo: "PHP",
              imagenUrl: LogoPhp,
              descripcion: "",
            },
    
            {
              titulo: "Figma",
              imagenUrl: LogoFigma,
              descripcion: "",
            },
    
            {
              titulo: "Trello",
              imagenUrl: LogoTrello,
            },
          ],
        };
      }render() {
      return (
        <React.Fragment>
          <div className="d-flex flex-row">
          {this.state.tarjetas.map((tarjeta) => (
            <SkillsPersonales
              imagenUrl={tarjeta.imagenUrl}
              titulo={tarjeta.titulo}
            />
          ))}
        </div>
        </React.Fragment>
       
      );
    }
  }
  
  export default Tarjeta;
