import React from "react";
import {
  Jumbotron,
  Container,
  Card,
  Button,
  ProgressBar,
} from "react-bootstrap";
import Footer from "./Footer";
import SkillsPersonales from "./SkillsPersonales";
import Captura from "../img/Captura.png";
import LogoJavascript from "../img/javascript.png";
import LogoHtml from "../img/html.png";
import LogoCss from "../img/css.png";

class Skills extends React.Component {
  constructor() {
    super();
    this.state = {
      skills: [
        {
          titulo: "HTML",
          imagenUrl: LogoHtml,
          descripcion: "",
          barra: "50",
        },
        {
          titulo: "CSS",
          imagenUrl: LogoCss,
          descripcion: "",
          barra: "50",
        },

        {
          titulo: "Javascript",
          imagenUrl: LogoJavascript,
          descripcion: "",
          barra: 70,
        },
      ],
    };
  }
  render() {
    return (
      <React.Fragment>
        <Jumbotron fluid className="bg-success">
          <Container>
            <h1>Skills</h1>
            <p>
              “Me había levantado para irme a casa; porque ya era tarde, y todo
              estaba oscuro”, El paseo, Robert Walser.
            </p>
          </Container>
        </Jumbotron>
        <div className="d-flex flex-row">
          {this.state.skills.map((skillsPersonales) => (
            <SkillsPersonales
              imagenUrl={tarjeta.imagenUrl}
              titulo={tarjeta.titulo}
              descripcion={tarjeta.descripcion}
              barra={tarjeta.barra}
            />
          ))}
        </div>
        <Footer />
      </React.Fragment>
    );
  }
}

export default Skills;
