import React from "react";
import { Card } from "react-bootstrap";

const SkillsPersonales = (props) => (
  <div className="d-flex justify-content-center items-center ">
    <Card style={{ width: "6rem", margin: "10px" }}>
      <Card.Img variant="top" src={props.imagenUrl} />
      <Card.Title className="m-3 d-flex justify-content-center items-center">
        {props.titulo}
      </Card.Title>

      <Card.Body>
        {/* <Card.Text className="text-dark pr-2" id="font">
          {props.descripcion}
        </Card.Text> */}
        
      </Card.Body>
    </Card>
  </div>
);

export default SkillsPersonales;
