import React from "react";
import { Jumbotron, Container } from "react-bootstrap";
import Footer from "./Footer";
import ProyectosPersonales from "./ProyectosPersonales";
import Viaje from "../img/viaje.jpg";
import Fula from "../img/fula.jpg";
import Asia from "../img/asia.jpg";
import JumbotronReact from "./JumbotronReact";

class Proyectos extends React.Component {
  constructor() {
    super();
    this.state = {
      tarjetas: [
        {
          titulo: "Iniciación al Fula",
          descripcion: "Editor en el libro. El objetivo de este libro de iniciación es ayudar a las personas que quieran aprender la lengua y la cultura fula.",
          imagenUrl: Fula,
          fecha: "20/20/2019",
          enlace: "https://github.com/martindejos"
        },

        {
          titulo: "El siglo de Asia",
          descripcion: "Colaborador y editor en el libro de Mateo Madridejos. Encargado de la organización, aspectos de edición, contacto con la editorial y promoción.",
          imagenUrl: Asia,
          fecha: "20/20/2019",

        },

        {
          titulo: "Viaje por la última frontera de Europa",
          descripcion: "Un libro de aventuras con historia, política y reflexiones viajeras, sobre mis experiencias en un viaje de cuatro meses por las fronteras de Europa. Recorrí lugares poco transitados como Kaliningrado, Minsk, el este de Ucrania, Transnistria, Gagauzia, el País Sículo, el Kurdistán turco y Chipre del Norte.",
          imagenUrl: Viaje,
          fecha: "20/20/2019",

        },
        ],
    };
  }
  
  render() {
    return (
      <React.Fragment>
        <JumbotronReact />
        
        <div className="d-flex justify-content-center align-items-center flex-column " >
          {this.state.tarjetas.map((tarjeta) => (
            <ProyectosPersonales
              imagenUrl={tarjeta.imagenUrl}
              titulo={tarjeta.titulo}
              descripcion={tarjeta.descripcion}
              fecha={tarjeta.fecha}
              
            />
          ))}
        </div>
        <Footer /> 
      </React.Fragment>
     
    );
  }
}

export default Proyectos;
