import React from "react";
import Skills from "./Skills";
import Footer from "./Footer";
import ProyectosPersonales from "./ProyectosPersonales";
import EstudiosPersonales from "./EstudiosPersonales";
import JumbotronReact from "./JumbotronReact";
import { Card } from "react-bootstrap";
import DrunkBee from "../img/drunkbee.png";
import Consultora from "../img/consultora.png";

class Proyectos extends React.Component {
  constructor() {
    super();
    this.state = {
      desarrolloWeb: [
        {
          titulo: "Portfolio personal",
          descripcion: "Desarrollada con React",
          imagenUrl: Consultora,
          fecha: "XX",
          enlace: "https://github.com/losTinchos/Consultora",
        },
        {
          titulo: "Badalona Jiu Jitsu",
          descripcion: "Estilos propios con algo de Boostrap",
          imagenUrl: Consultora,
          fecha: "XX",
          enlace: "https://github.com/losTinchos/Consultora",
        },
        {
          titulo: "Consultoría LosTinchos",
          descripcion:
            "Aplicación 'CRUD' Web en PHP que permita pedir cita a los desarrolladores y equipos para solucionar problemas técnicos con la ayuda del formador o CTO. Frontend: HTML, CSS, Bootstrap; Backend: PHP; Bases de datos: MySQL.",
          imagenUrl: Consultora,
          fecha: "XX",
          enlace: "https://github.com/losTinchos/Consultora",
        },
        {
          titulo: "Wheel of Doom TheDrunkBee",
          descripcion:
            "The Drunk Bee is a multiplatform application (suitable for mobile phones, tablets and computers) designed by Los Tinchos that allows you to play a sacrifice selection system. Tecnologías: HTML, CSS, JS, GitHub. Está en producción en GH pages",
          imagenUrl: DrunkBee,
          fecha: "XX",
          enlace: "https://github.com/martindejos/TheDrunkBee",
        },
      ],
      tarjetas: [
        {
          titulo: "Bootcamp Full-Stack Web Development",
          descripcion: "Factoria F5",
          anos: "Abril 2021-Actualmente",
          horas: "850h",
        },
        {
          titulo: "Bootcamp Front-End Web Development",
          descripcion: "Factoria F5",
          anos: "Enero 2021-Marzo 2021",
          horas: "300h",
        },
        
      ],
      skills: [
        {
          tecnologia1: "Vue",
          tecnologia2: "Vue",
          tecnologia3: "Vue",
          tecnologia4: "Vue",
          tecnologia5: "Vue",
        },
        {
          tecnologia1: "Vue",
          tecnologia2: "Vue",
          tecnologia3: "Vue",
          tecnologia4: "Vue",
          tecnologia5: "Vue",
        },
        {
          tecnologia1: "Vue",
          tecnologia2: "Vue",
          tecnologia3: "Vue",
          tecnologia4: "Vue",
          tecnologia5: "Vue",
        },
       
        
      ],
    };
  }

  render() {
    return (
      <React.Fragment>
        <JumbotronReact />
        <h3 className="m-4 d-flex justify-content-center">Skills</h3>

        <div className=" d-flex flex-sm-row flex-column align-items-center justify-content-center">
               
        </div>

        <div>
        <Card className="p-3 ">
          <Card.Title
            className="mt-4 mb-5 d-flex  h2 justify-content-center align-items-center"
            style={{ margin: "" }}
          >
            HTML - CSS - Javascript - React - Vue -
          </Card.Title>
          <Card.Title
            className="mt-2 mb-5 d-flex  h2 justify-content-center align-items-center"
            style={{ margin: "" }}
          >
            Bootstrap - Tailwinds - Saas
          </Card.Title>
          <Card.Title
            className="mt-2 mb-5 d-flex  h2 justify-content-center align-items-center"
            style={{ margin: "" }}
          >
            PHP - Laravel - MySql
          </Card.Title>
          <Card.Title
            className="mt-2 mb-5 d-flex  h2 justify-content-center align-items-center "
            style={{ margin: "5px" }}
          >
            GitHub - Trello - Figma - Canva
          </Card.Title>
          <Card.Title
            className="mt-2 mb-5 d-flex  h2 justify-content-center align-items-center "
            style={{ margin: "5px" }}
          >
            Agile - Kanban
          </Card.Title>
        </Card>
        </div>
        <h2 className="mt-5 d-flex justify-content-center">
          Formación en desarrollo web
        </h2>

        <div className=" d-flex flex-sm-row flex-column align-items-center justify-content-center">
          {this.state.skills.map((tarjeta) => (
            <Skills
              tecnologia1={tarjeta.tecnologia1}
              tecnologia2={tarjeta.tecnologia1}
              tecnologia3={tarjeta.tecnologia1}
              tecnologia1={tarjeta.tecnologia1}
              tecnologia1={tarjeta.tecnologia1}
                    />
          ))}
        </div>
        
             
        <div className=" d-flex flex-sm-row flex-column align-items-center justify-content-center">
          {this.state.tarjetas.map((tarjeta) => (
            <EstudiosPersonales
              titulo={tarjeta.titulo}
              descripcion={tarjeta.descripcion}
              anos={tarjeta.anos}
              horas={tarjeta.horas}
            />
          ))}
        </div>

        <div className="d-flex justify-content-center align-items-center flex-column mt-5">
          <h2 className="m-4">Desarrollo web</h2>
          {this.state.desarrolloWeb.map((desarrolloWeb) => (
            <ProyectosPersonales
              imagenUrl={desarrolloWeb.imagenUrl}
              titulo={desarrolloWeb.titulo}
              enlace={desarrolloWeb.enlace}
              descripcion={desarrolloWeb.descripcion}
              fecha={desarrolloWeb.fecha}
            />
          ))}
        </div>
        <Footer />
      </React.Fragment>
    );
  }
}

export default Proyectos;
